// src/components/WorkSession.js
import React, { useState } from 'react';

function WorkSession({ tasks }) {
  const [selectedIds, setSelectedIds] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [seconds, setSeconds] = useState(0);

  const toggleSelect = (taskId) => {
    setSelectedIds(prev =>
      prev.includes(taskId) ? prev.filter(id => id !== taskId) : [...prev, taskId]
    );
  };

  const selectedTasks = tasks.filter(task => selectedIds.includes(task.id));
  const totalMinutes = selectedTasks.reduce((sum, t) => sum + t.minutes, 0);

  const startSession = () => {
    setIsRunning(true);
    setSeconds(totalMinutes * 60);
  };

  return (
    <div>
      <h2>Work Session</h2>
      {!isRunning ? (
        <>
          <ul>
            {tasks.map(task => (
              <li key={task.id}>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedIds.includes(task.id)}
                    onChange={() => toggleSelect(task.id)}
                  />
                  {task.title} - {task.minutes} min
                </label>
              </li>
            ))}
          </ul>
          <p>Total Time: {totalMinutes} minutes</p>
          <button onClick={startSession} disabled={selectedIds.length === 0}>
            Start Work Session
          </button>
        </>
      ) : (
        <Timer seconds={seconds} />
      )}
    </div>
  );
}

function Timer({ seconds }) {
  const [timeLeft, setTimeLeft] = useState(seconds);

  React.useEffect(() => {
    const interval = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          clearInterval(interval);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const format = (s) => {
    const m = Math.floor(s / 60);
    const sec = s % 60;
    return `${m}:${sec.toString().padStart(2, '0')}`;
  };

  return (
    <div>
      <h3>Time Left: {format(timeLeft)}</h3>
    </div>
  );
}

export default WorkSession;
