import React, { useState, useEffect } from 'react';

import { useNavigate } from 'react-router-dom';

function TaskList() {
  const navigate = useNavigate();

  return (
    <div>
      <h1>Task List</h1>
      <button onClick={() => navigate('/work-session')}>
        Go to Work Session
      </button>
      {/* rest of your task list code */}
    </div>
  );
}

function WorkSession({ tasks }) {
  const [selectedOrder, setSelectedOrder] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [newBreakDuration, setNewBreakDuration] = useState(5);

  const toggleTask = (task) => {
    const alreadySelected = selectedOrder.find(item => item.type === 'task' && item.id === task.id);
    if (alreadySelected) {
      setSelectedOrder(prev => prev.filter(item => !(item.type === 'task' && item.id === task.id)));
    } else {
      setSelectedOrder(prev => [...prev, { ...task, type: 'task' }]);
    }
  };

  const addBreakAfter = (index) => {
    const breakItem = {
      type: 'break',
      title: `${newBreakDuration}-minute break`,
      minutes: newBreakDuration,
    };
    const updated = [...selectedOrder];
    updated.splice(index + 1, 0, breakItem);
    setSelectedOrder(updated);
  };

  const startSession = () => {
    if (selectedOrder.length === 0) return;
    setIsRunning(true);
    setCurrentIndex(0);
    setSeconds(selectedOrder[0].minutes * 60);
  };

  useEffect(() => {
    if (!isRunning) return;

    const interval = setInterval(() => {
      setSeconds(prev => {
        if (prev <= 1) {
          clearInterval(interval);
          if (currentIndex + 1 < selectedOrder.length) {
            const next = selectedOrder[currentIndex + 1];
            setCurrentIndex(i => i + 1);
            setSeconds(next.minutes * 60);
          } else {
            setIsRunning(false);
            setCurrentIndex(0);
            setSeconds(0);
          }
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [isRunning, seconds, currentIndex, selectedOrder]);

  const formatTime = (s) => {
    const m = Math.floor(s / 60);
    const sec = s % 60;
    return `${m}:${sec.toString().padStart(2, '0')}`;
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Work Session</h2>

      {!isRunning && (
        <>
          <h3>Select Tasks:</h3>
          {tasks.map(task => (
            <label key={task.id} style={{ display: 'block', margin: '5px 0' }}>
              <input
                type="checkbox"
                checked={selectedOrder.some(item => item.type === 'task' && item.id === task.id)}
                onChange={() => toggleTask(task)}
              />
              {task.title} ({task.minutes} min)
            </label>
          ))}

          <h4 style={{ marginTop: 20 }}>Session Order:</h4>
          <ul>
            {selectedOrder.map((item, i) => (
              <li key={i}>
                {item.type === 'task' ? ` ${item.title}` : `️ ${item.title}`}
                {' '}
                {item.minutes} min
                {item.type === 'task' && (
                  <button
                    onClick={() => addBreakAfter(i)}
                    style={{ marginLeft: 10 }}
                  >
                    + Add Break After
                  </button>
                )}
              </li>
            ))}
          </ul>

          <div style={{ marginTop: 10 }}>
            <label>
              Break duration (min):{' '}
              <input
                type="number"
                value={newBreakDuration}
                onChange={e => setNewBreakDuration(Number(e.target.value))}
                min={1}
                style={{ width: 50 }}
              />
            </label>
          </div>

          <button style={{ marginTop: 20 }} onClick={startSession}>
            Start Session
          </button>
        </>
      )}

      {isRunning && selectedOrder[currentIndex] && (
        <div>
          <h3>
            {selectedOrder[currentIndex].type === 'task'
              ? `Task: ${selectedOrder[currentIndex].title}`
              : `Break: ${selectedOrder[currentIndex].title}`}
          </h3>
          <h1>{formatTime(seconds)}</h1>
          <p>Step {currentIndex + 1} of {selectedOrder.length}</p>
        </div>
      )}
    </div>
  );
}

export default WorkSession;
