import React from 'react';

function Calendar({ tasks }) {
  const today = new Date();
  const currentYear = today.getFullYear();
  const currentMonth = today.getMonth(); // 0-indexed (0 = Jan)

  const dueColors = {
    'due-today': '#ffcccc',  // light red
    'due-soon': '#fff5cc',   // light yellow
    'due-later': '#d4edda',  // light green
  };

  const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
  const firstDayOfMonth = new Date(currentYear, currentMonth, 1).getDay(); // 0 = Sunday

  // Group tasks by dueDate
  const tasksByDate = {};
  tasks.forEach(task => {
    const date = task.dueDate;
    if (!tasksByDate[date]) tasksByDate[date] = [];
    tasksByDate[date].push(task);
  });

  const calendarDays = [];

  // Fill empty cells before the 1st of the month
  for (let i = 0; i < firstDayOfMonth; i++) {
    calendarDays.push(<td key={`empty-${i}`} />);
  }

  // Fill in days with task data
  for (let day = 1; day <= daysInMonth; day++) {
    const dateStr = new Date(currentYear, currentMonth, day).toISOString().split('T')[0]; // yyyy-mm-dd
    const dayTasks = tasksByDate[dateStr] || [];

    calendarDays.push(
      <td key={day} style={{ verticalAlign: 'top', padding: 10, border: '1px solid #ccc', height: 100 }}>
        <strong>{day}</strong>
        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
          {dayTasks.map((task, index) => (
            <li key={index} style={{ fontSize: 12, background: '#eef', marginTop: 4, padding: 2, borderRadius: 4 }}>
              {task.title}
            </li>
          ))}
        </ul>
      </td>
    );
  }

  // Group into weeks (7 days per row)
  const rows = [];
  for (let i = 0; i < calendarDays.length; i += 7) {
    rows.push(<tr key={i}>{calendarDays.slice(i, i + 7)}</tr>);
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>ðŸ“… Calendar for {today.toLocaleString('default', { month: 'long' })} {currentYear}</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Sun</th><th>Mon</th><th>Tue</th><th>Wed</th>
            <th>Thu</th><th>Fri</th><th>Sat</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
    </div>
  );
}

export default Calendar;
