import React, { useState, useEffect } from 'react';

function WorkSession({ tasks = [] }) {
  const [selectedOrder, setSelectedOrder] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [newBreakDuration, setNewBreakDuration] = useState(5);

  const toggleTask = (task) => {
    const alreadySelected = selectedOrder.find(item => item.type === 'task' && item.id === task.id);
    if (alreadySelected) {
      setSelectedOrder(prev => prev.filter(item => !(item.type === 'task' && item.id === task.id)));
    } else {
      setSelectedOrder(prev => [...prev, { ...task, type: 'task' }]);
    }
  };

  const getDueClass = (dueDateStr) => {
    if (!dueDateStr) return '';
    const today = new Date();
    const due = new Date(dueDateStr);
    today.setHours(0,0,0,0);
    due.setHours(0,0,0,0);
    const diff = (due - today) / (1000*60*60*24);

    if (diff < 0) return 'due-today';   // overdue or today
    if (diff === 0) return 'due-today';
    if (diff <= 3) return 'due-soon';
    return 'due-later';
  };

  const addBreakAfter = (index) => {
    const breakItem = {
      type: 'break',
      title: `${newBreakDuration}-minute break`,
      minutes: newBreakDuration,
    };
    const updated = [...selectedOrder];
    updated.splice(index + 1, 0, breakItem);
    setSelectedOrder(updated);
  };

  const startSession = () => {
    if (selectedOrder.length === 0) return;
    setIsRunning(true);
    setCurrentIndex(0);
    setSeconds(selectedOrder[0].minutes * 60);
  };

  useEffect(() => {
    if (!isRunning) return;

    const interval = setInterval(() => {
      setSeconds(prev => {
        if (prev <= 1) {
          clearInterval(interval);
          if (currentIndex + 1 < selectedOrder.length) {
            const next = selectedOrder[currentIndex + 1];
            setCurrentIndex(i => i + 1);
            setSeconds(next.minutes * 60);
          } else {
            setIsRunning(false);
            setCurrentIndex(0);
            setSeconds(0);
          }
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [isRunning, seconds, currentIndex, selectedOrder]);

  const formatTime = (s) => {
    const m = Math.floor(s / 60);
    const sec = s % 60;
    return `${m}:${sec.toString().padStart(2, '0')}`;
  };

  return (
    <div style={{ padding: 20, backgroundColor: '#f7f7f7', minHeight: '100vh' }}>
      <h2 style={{ textAlign: 'center', marginBottom: 30 }}>Work Session</h2>

      {!isRunning && (
        <div style={{ maxWidth: 800, margin: 'auto' }}>
          <h3>Select Tasks:</h3>
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
              gap: 15,
              marginBottom: 30,
            }}
          >
            {tasks.map(task => {
              const selected = selectedOrder.some(item => item.type === 'task' && item.id === task.id);
              return (
                <label
                  key={task.id}
                  className={getDueClass(task.dueDate)}
                  style={{
                    border: '1px solid #ccc',
                    borderRadius: 8,
                    padding: 15,
                    backgroundColor: selected ? '#d0f0fd' : 'white',
                    cursor: 'pointer',
                    userSelect: 'none',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    gap: 8,
                  }}
                >
                  <input
                    type="checkbox"
                    checked={selected}
                    onChange={() => toggleTask(task)}
                    style={{ alignSelf: 'flex-start' }}
                  />
                  <strong>{task.title}</strong>
                  <span>{task.minutes} min</span>
                  <span>Due: {task.dueDate}</span>
                </label>
              );
            })}
          </div>

          <h4>Session Order:</h4>
          <ul style={{ listStyleType: 'none', paddingLeft: 0, maxWidth: 600, margin: 'auto' }}>
            {selectedOrder.map((item, i) => (
              <li
                key={i}
                style={{
                  border: '1px solid #aaa',
                  borderRadius: 6,
                  padding: 12,
                  marginBottom: 12,
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  backgroundColor: item.type === 'task' ? '#f0ead8' : '#e8f4fd',
                }}
              >
                <div>
                  <strong>{item.type === 'task' ? 'Task:' : 'Break:'}</strong> {item.title} â€” {item.minutes} min
                </div>
                {item.type === 'task' && (
                  <button
                    onClick={() => addBreakAfter(i)}
                    style={{
                      backgroundColor: '#4CAF50',
                      border: 'none',
                      borderRadius: 4,
                      color: 'white',
                      padding: '6px 10px',
                      cursor: 'pointer',
                    }}
                  >
                    + Add Break After
                  </button>
                )}
              </li>
            ))}
          </ul>

          <div style={{ maxWidth: 600, margin: 'auto' }}>
            <label style={{ display: 'block', marginBottom: 10 }}>
              Break duration (min):
              <input
                type="number"
                value={newBreakDuration}
                onChange={e => setNewBreakDuration(Number(e.target.value))}
                min={1}
                style={{
                  width: 60,
                  padding: 6,
                  marginLeft: 10,
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              />
            </label>

            <button
              style={{
                marginTop: 15,
                width: '100%',
                padding: 14,
                fontSize: 16,
                backgroundColor: '#007bff',
                border: 'none',
                borderRadius: 6,
                color: 'white',
                cursor: 'pointer',
              }}
              onClick={startSession}
              disabled={selectedOrder.length === 0}
            >
              Start Session
            </button>
          </div>
        </div>
      )}

      {isRunning && selectedOrder[currentIndex] && (
        <div
          style={{
            maxWidth: 600,
            margin: '40px auto',
            padding: 30,
            borderRadius: 8,
            backgroundColor: '#fff',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            textAlign: 'center',
          }}
        >
          <h3>
            {selectedOrder[currentIndex].type === 'task'
              ? `Task: ${selectedOrder[currentIndex].title}`
              : `Break: ${selectedOrder[currentIndex].title}`}
          </h3>
          <h1 style={{ fontSize: 90, margin: '20px 0' }}>{formatTime(seconds)}</h1>
          <p>Step {currentIndex + 1} of {selectedOrder.length}</p>
        </div>
      )}
    </div>
  );
}

export default WorkSession;
